var cross = document.getElementsByTagName("cross")[0].innerHTML;
var rosary = document.getElementsByTagName("rosary")[0].innerHTML;
var confession = document.getElementsByTagName("confession")[0].innerHTML;
var bible = document.getElementsByTagName("bible")[0].innerHTML;

if (rosary == null || rosary == undefined){
  console.log('No <rosary> tags found');
}
else{
  if (rosary.includes("href")){
    document.getElementsByTagName("rosary")[0].innerHTML = document.getElementsByTagName("rosary")[0].innerHTML.replace("href", "");
  } 
}

if (cross == null || cross == undefined){
  console.log('No <cross> tags found');
}
else{
  if (cross.includes("<")){
    document.getElementsByTagName("cross")[0].innerHTML = document.getElementsByTagName("cross")[0].innerHTML.replace( /(<([^>]+)>)/ig, '');
  }
}

if (confession == null || confession == undefined){
  console.log('No <confession> tags found');
}
else{
  $("confession").children().each(function() {
    while(this.attributes.length > 0){
        this.removeAttribute(this.attributes[0].name);
    }
  });
}

if (bible == null || bible == undefined){
  console.log('No <bible> tags found');
}
else{
  if (bible.includes("<script>")){
    document.getElementsByTagName("bible")[0].innerHTML = document.getElementsByTagName("bible")[0].innerHTML.replace("<script>", "");
  }
}

