var cross = document.getElementsByTagName("crucifix")[0].innerHTML;
var rosary = document.getElementsByTagName("rosary")[0].innerHTML;
var confession = document.getElementsByTagName("confession")[0].innerHTML;

var input_tags = document.getElementsByTagName("input");

var each_variable = 0;

if (rosary == null || rosary == undefined){
  console.log('No <rosary> tags found');
}
else{
  if (rosary.includes("href")){
    document.getElementsByTagName("rosary")[0].innerHTML = document.getElementsByTagName("rosary")[0].innerHTML.replace("href", "");
  } 
}

if (cross == null || cross == undefined){
  console.log('No <cross> tags found');
}
else{
  if (cross.includes("<")){
    document.getElementsByTagName("crucifix")[0].innerHTML = document.getElementsByTagName("crucifix")[0].innerHTML.replace(/(<([^>]+)>)/ig, '');
  }
}

if (confession == null || confession == undefined){
  console.log('No <confession> tags found');
}
else{
  $("confession").children().each(function() {
    while(this.attributes.length > 0){
        this.removeAttribute(this.attributes[0].name);
    }
  });
}

$("form").submit(function(){
  for (var i = 0; i < input_tags.length; i++) {
    if (input_tags[i].id == null || input_tags[i].id == undefined || input_tags[i].id == ''){
      console.log('There are no ID attributes in any of the inputs!');
      
      for (var i = 0; i < input_tags.length; i++) {
        var each_input = document.getElementsByTagName("input")[0];

        each_input.id = each_variable + 1;
        each_variable = each_variable + 1;
        document.getElementById(each_variable).value = document.getElementById(each_variable).value.replace(/(<([^>]+)>)/ig, "");
      }
    }
    else{
      for (var i = 0; i < input_tags.length; i++) {
        document.getElementById(input_tags[i].id).value = document.getElementById(input_tags[i].id).value.replace(/(<([^>]+)>)/ig, "");
      }
    }
  }
});

function bless(str) {
  return str.replace(/(<([^>]+)>)/ig, "");
}